package edu.njucm.book.common.util;

import org.slf4j.Logger;
import org.slf4j.LoggerFactory;

import javax.crypto.Cipher;
import javax.crypto.spec.SecretKeySpec;

import java.security.Key;
import java.util.Base64;

/**
 * 加解密工具类
 *
 * @author lvrongwang
 * @since 2020/3/21 14:14
 */
public class EncryptUtils {

    private static final Logger logger = LoggerFactory.getLogger(EncryptUtils.class);

    private static String defaultSecretKey = "default_secret_key"; //默认密钥
    private static Cipher encryptCipher; //加密器
    private static Cipher decryptCipher; //解密器

    public EncryptUtils() throws Exception {
        this(defaultSecretKey);
    }

    /**
     * @param secretKey 加密解密使用的密钥
     */
    public EncryptUtils(String secretKey) {
        Key key;
        try {
            key = getKey(secretKey.getBytes());
            encryptCipher = Cipher.getInstance("DES");
            encryptCipher.init(Cipher.ENCRYPT_MODE, key);
            decryptCipher = Cipher.getInstance("DES");
            decryptCipher.init(Cipher.DECRYPT_MODE, key);
        } catch (Exception e) {
            e.printStackTrace();
        }
    }

    /**
     * 加密
     * 逻辑：1. 将要加密的字符串转换为字节数组(byte array)
     *      2. 将第一步的字节数组作为输入使用加密器(Cipher)的doFinal方法进行加密, 返回字节数组
     *      3. 把加密后的字节数组转换成十六进制的字符串
     *
     * @param strIn 要加密的字符串
     * @return 返回加密后的十六进制字符串
     * @throws Exception
     */
    public static String encrypt(String strIn) {
        try {
            EncryptUtils u = new EncryptUtils();
            return byteArr2HexStr(u.encrypt(strIn.getBytes()));
        } catch (Exception e) {
            e.printStackTrace();
        }
        return "";
    }

    private byte[] encrypt(byte[] arrB) throws Exception {
        return encryptCipher.doFinal(arrB);
    }

    /**
     * 解密
     * 逻辑：1. 把加密后的十六进制字符串转换成字节数组(byte array)
     *      2. 将第一步的字节数组作为输入使用加密器(Cipher)的doFinal方法进行解密, 返回字节数组(byte array)
     *      3. 把解密后的字节数组转换成字符串
     *
     * @param strIn
     * @return
     * @throws Exception
     */
    public static String decrypt(String strIn) {
        try {
            EncryptUtils u = new EncryptUtils();
            return new String(u.decrypt(hexStr2ByteArr(strIn)));
        } catch (Exception e) {
            e.printStackTrace();
            logger.error("msg:", e);
        }
        return "";
    }

    private byte[] decrypt(byte[] arrB) throws Exception {
        return decryptCipher.doFinal(arrB);
    }

    private static String byteArr2HexStr(byte[] arrB) throws Exception {
        int iLen = arrB.length;
        // 每个byte用两个字符才能表示，所以字符串的长度是数组长度的两倍
        StringBuilder stringBuilder = new StringBuilder(iLen * 2);
        for (int b : arrB) {
            int intTmp = b;
            // 把负数转换为正数
            while (intTmp < 0) {
                intTmp = intTmp + 256;
            }
            // 小于0F的数需要在前面补0
            if (intTmp < 16) {
                stringBuilder.append("0");
            }
            stringBuilder.append(Integer.toString(intTmp, 16));
        }
        return stringBuilder.toString();
    }

    private static byte[] hexStr2ByteArr(String strIn) throws Exception {
        byte[] arrB = strIn.getBytes();
        int iLen = arrB.length;
        // 两个字符表示一个字节，所以字节数组长度是字符串长度除以2
        byte[] arrOut = new byte[iLen / 2];
        for (int i = 0; i < iLen; i = i + 2) {
            String strTmp = new String(arrB, i, 2);
            arrOut[i / 2] = (byte) Integer.parseInt(strTmp, 16);
        }
        return arrOut;
    }

    private Key getKey(byte[] arrBTmp) throws Exception {
        // 创建一个空的8位字节数组（默认值为0）
        byte[] arrB = new byte[8];
        // 将原始字节数组转换为8位
        for (int i = 0; i < arrBTmp.length && i < arrB.length; i++) {
            arrB[i] = arrBTmp[i];
        }
        // 生成密钥
        return new SecretKeySpec(arrB, "DES");
    }

    /**
     * Base64加密
     *
     * @param bytes
     * @return
     */
    public static String base64Encrypt(byte[] bytes) {
        try {
            Base64.Encoder encoder = Base64.getEncoder();
            return new String(encoder.encode(bytes));
        } catch (Exception e) {
            logger.error("base64 encrypt error" + e);
        }
        return null;
    }

    /**
     * Base64解密
     *
     * @param base64Str
     * @return
     */
    public static byte[] base64Decrypt(String base64Str) {
        try {
            Base64.Decoder decoder = Base64.getDecoder();
            return decoder.decode(base64Str);
        } catch (Exception e) {
            logger.error("base64 encrypt error" + e);
        }
        return null;
    }

    public static void main(String[] args) {
        try {
            String test = "";
            System.out.println("解密前的字符：" + test);
//            System.out.println("加密后的字符：" + encrypt(test));
//            System.out.println("解密后的字符：" + decrypt(encrypt(test)));
//            System.out.println("加密后的字符：" + base64Encrypt(test.getBytes()));
//            System.out.println("解密后的字符：" + new String(base64Decrypt(base64Encrypt(test.getBytes()))));
            System.out.println("解密后的字符：" + base64Decrypt(test));
            System.out.println("加密后的字符：" + new String(base64Decrypt(base64Encrypt(test.getBytes()))));
        } catch (Exception e) {
            e.printStackTrace();
        }
    }
}
